import React from "react";
import "./index.scss";
import { Collection } from "./Components/Collection";
import axios from "axios";

function App() {
  const categories = [
    { name: "Все" },
    { name: "Море" },
    { name: "Горы" },
    { name: "Архитектура" },
    { name: "Города" },
  ];

  const [photos, setPhotos] = React.useState([]);
  const [categoriesActive, setCategoriesActive] = React.useState(0);
  const [searchValue, setSearchValue] = React.useState("");
  const params = categoriesActive > 0 ? `category=${categoriesActive}` : ``;

  console.log(categoriesActive);
  React.useEffect(() => {
    axios
      .get(`https://62ffc93b9350a1e548e663ec.mockapi.io/items/photos?${params}`)
      .then((obj) => setPhotos(obj.data));
  }, [categoriesActive]);

  return (
    <div className="App">
      <h1>Моя коллекция фотографий</h1>
      <div className="top">
        <ul className="tags">
          {categories.map((item, i) => (
            <li
              onClick={() => setCategoriesActive(i)}
              className={categoriesActive === i ? "active" : ""}
              key={i}
            >
              {item.name}
            </li>
          ))}
        </ul>
        <input
          className="search-input"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
          placeholder="Поиск по названию"
        />
      </div>
      <div className="content">
        {photos
          .filter((item) => {
            if (item.name.toLowerCase().includes(searchValue.toLowerCase())) {
              return true;
            }
            return false;
          })
          .map((item, i) => (
            <Collection key={i} name={item.name} images={item.photos} />
          ))}
      </div>
      <ul className="pagination">
        <li>1</li>
        <li className="active">2</li>
        <li>3</li>
      </ul>
    </div>
  );
}

export default App;
